import React, { useEffect, useState } from "react";
import { Collapse } from "antd";
import { getWeather } from "../API/api-services";
import DropDown from './share/dropdown'
const { Panel } = Collapse;
function callback(key) {
  console.log(key);
}
const text = `
  A dog is a type of domesticated animal.
  Known for its loyalty and faithfulness,
  it can be found as a welcome guest in many households across the world.
`;
const CurrentWeatherForcst = ({}) => {
  const [item, setItem] = useState(["current"]);  
  const [location, setLocation] = useState("");
  const [cloudes, setCloudes] = useState("");
  const [temp, setTemp] = useState("");
  const [windSpeed, setwindSpeed] = useState("");
  const [currWeatherData, setcurrWeatherData] = useState("");
  const [dailyWeatherData, setcurrdailyWeatherData] = useState("");

  const success = (position) => {
    const latitude = position.coords.latitude;
    const longitude = position.coords.longitude;
    let ItemArray = []
    let queryParams = {};
    queryParams.lat = latitude;
    queryParams.lon = longitude;
    let d 
    let n
    let day

    const GetDaYForDalilyData = (n) => {
        switch (n) {
            case 0:
              day = "Sunday"+   n;
              return day;
            case 1:
              day = "Monday" + n;
              return day + n;
            case 2:
              day = "Tuesday" + n;
              return day;
            case 3:
              day = "Wednesday" + n;
              return day;
            case 4:
              day = "Thursday" + n;
              return day;
            case 5:
              day = "Friday" + n;
              return day;
            case 6:
              day = "Saturday";
              return day
          }


    }

    getWeather(queryParams)
      .then((response) => {
        setLocation(response.data.timezone);
        response.data.daily.map((result,i) => {
            d = new Date(result.dt*1000);
            n = d.getDay();
            day = GetDaYForDalilyData(n)
            if(i !== 7){
                ItemArray.push(day)
            }
        })
        // setItem(response.data.current.daily)
        console.log("response", response.data.daily);
        console.log("ItemArray",ItemArray)
      })
      .catch((error) => {
        console.log(error);
      });

    console.log(latitude, longitude);
  };
  const error = () => {
    alert("Unable to retrieve your location");
  };

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(success, error);
  }, []);

  return (
    <>
      <DropDown key = "current" DropDownText="Current" Item={item}/>
      <DropDown key = "daily" DropDownText="Daily" />
      <DropDown key = "weekly" DropDownText="Weekly" />

      <Collapse onChange={callback}>
        <Panel header="This is panel header 2" key="2">
          <p>{text}</p>
        </Panel>
        <Panel header="This is panel header 3" key="3">
          <p>{text}</p>
        </Panel>
      </Collapse>
    </>
  );
};
export default CurrentWeatherForcst;
